import "./App.css";
import { useState, useEffect } from "react";
import Axios from "axios";
import { OutlinedInput, Button, useTheme, Typography, Box, Stack } from '@mui/material';


function App() {
  const [listOfUsers, setListOfUsers] = useState([]);
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [username, setUsername] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/getUsers").then((response) => {
      setListOfUsers(response.data);
    });
  }, []);

  const createUser = () => {
    // object = req.body passing the data to backend
    Axios.post("http://localhost:3001/createUser", {
      name,
      age,
      username,
    }).then((response) => {
      // add the new user to the end of user list
      setListOfUsers([
        ...listOfUsers,
        {
          name,
          age,
          username,
        },
      ]);
    });
  };

  return (
    <div className="App">

      <Box
      >
        <Typography variant="h4">
          InfoCard
        </Typography>
      </Box>
      <Stack gap={2} direction="col" marginLeft={50} marginTop={2}>
        <OutlinedInput
          type="text"
          sx ={
            {
              borderColor: 'primary.main',
              height: '35px'
            }
          }
          placeholder="Name..."
          gap="1rem"
          color='primary'
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <OutlinedInput
          type="number"
          sx ={
            {
              borderColor: 'primary.main',
              height: '35px'
            }
          }
          placeholder="Age..."
          size="small"
          onChange={(event) => {
            setAge(event.target.value);
          }}
        />
        <OutlinedInput
          type="text"
          sx ={
            {
              borderColor: 'primary.main',
              height: '35px'
            }
          }
          placeholder="Username..."
          gap="1rem"
          size="small"
          color='secondary'
          border={`1px solid ${useTheme().primary}`}
          onChange={(event) => {
            setUsername(event.target.value);
          }}
        />
        <Button
          variant="contained"
          size="small"
          onClick={createUser} > Create User </Button>
      </Stack>
      <div className="usersDisplay">
        {listOfUsers.map((user) => {
          return (
            <Box
             sx ={{
               width: "100px",
               height: "140px",
               border:"2px",
               borderColor:'primary.main',
               bgcolor:'primary.light'
             }}
            >
              <h1>Name: {user.name}</h1>
              <h1>Age: {user.age}</h1>
              <h1>Username: {user.username}</h1>
            </Box>
          );
        })}
      </div>


    </div>
  );
}

export default App;
